import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { FormInput, Button } from '../components';
import { useAuth } from '../utils/AuthContext';
import { useToast } from '../components/Toast';
import { Mail, Lock, User, Key, Calendar, Phone, Hash } from 'lucide-react';
import { generateStudentId, generateMentorId, previewStudentId, previewMentorId } from '../utils/idGenerator';
import styles from './Auth.module.css';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';

const registerSchema = z.object({
  firstName: z.string().min(1, 'First Name is required'),
  middleName: z.string().optional(),
  lastName: z.string().min(1, 'Last Name is required'),
  dob: z.string().min(1, 'Date of Birth is required'),
  mobileNo: z.string().min(10, 'Valid Mobile Number is required'),
  email: z.string().email('Invalid email format'),
  password: z.string().min(6, 'Password must be at least 6 characters'),
  confirmPassword: z.string(),
  adminId: z.string().optional(),
  agreeToTerms: z.literal(true, {
    errorMap: () => ({ message: 'You must agree to terms' }),
  }),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords do not match",
  path: ["confirmPassword"],
});

export const RegisterPage = () => {
  const {
    register,
    handleSubmit,
    watch,
    formState: { errors, isSubmitting },
  } = useForm({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      firstName: '',
      middleName: '',
      lastName: '',
      dob: '',
      mobileNo: '',
      email: '',
      password: '',
      confirmPassword: '',
      adminId: '',
      agreeToTerms: false,
    }
  });

  const [selectedRole, setSelectedRole] = useState('student');
  const [previewId, setPreviewId] = useState('');
  const [customError, setCustomError] = useState('');

  const firstName = watch('firstName');
  const middleName = watch('middleName');
  const lastName = watch('lastName');

  // Update live preview ID
  useEffect(() => {
    const fn = firstName || '';
    const mn = middleName ? middleName + ' ' : '';
    const ln = lastName || '';
    const fullName = `${fn} ${mn}${ln}`.trim();

    if (selectedRole === 'student') {
      setPreviewId(previewStudentId());
    } else if (selectedRole === 'mentor') {
      setPreviewId(previewMentorId(fullName));
    }
  }, [selectedRole, firstName, middleName, lastName]);

  const navigate = useNavigate();
  const { registerUser } = useAuth();
  const { addToast } = useToast();

  const onSubmit = (data) => {
    // Custom validation for Mentor adminId
    if (selectedRole === 'mentor' && (!data.adminId || data.adminId.trim() === '')) {
      setCustomError('University Admin ID is required for mentors');
      return;
    }
    setCustomError('');

    return new Promise((resolve) => {
      setTimeout(() => {
        const fn = data.firstName;
        const mn = data.middleName ? data.middleName + ' ' : '';
        const ln = data.lastName;
        const fullName = `${fn} ${mn}${ln}`.trim();

        const user = registerUser({
          ...data,
          name: fullName,
          role: selectedRole,
          adminId: selectedRole === 'mentor' ? data.adminId : null
        });

        addToast(`Registration successful! Your ID is: ${user.id}`, 'success');
        navigate(`/${selectedRole === 'admin' ? 'admin' : selectedRole}/dashboard`);
        resolve();
      }, 1000);
    });
  };

  return (
    <div className={styles.pageWrapper}>
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.6 }}
        className={styles.authCard}
      >
        {/* Header */}
        <div className={styles.header}>
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.2, type: 'spring' }}
            className={styles.iconWrapper}
          >
            <User size={32} color="white" />
          </motion.div>
          <h1 className={styles.title}>Join SAAMS</h1>
          <p className={styles.subtitle}>Create your account and start today</p>
        </div>

        {/* Role Selection */}
        <div className={styles.roleSelection}>
          <label className={styles.roleLabel}>Account Type</label>
          <div className={styles.roleGrid}>
            {['student', 'mentor'].map((role) => (
              <button
                key={role}
                type="button"
                onClick={() => setSelectedRole(role)}
                className={`${styles.roleButton} ${selectedRole === role ? styles.roleButtonActive : ''}`}
              >
                {role === 'student' ? 'ðŸ‘¨â€ðŸŽ“ Student' : 'ðŸ‘¨â€ðŸ« Mentor'}
              </button>
            ))}
          </div>
          <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', marginTop: '8px', textAlign: 'center' }}>
            â„¹ï¸ Your Unique ID is auto-assigned continuously and locked upon registration.
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit(onSubmit)} className={styles.form}>
          <FormInput
            id="previewId"
            label="Unique ID (Auto-Generated)"
            type="text"
            value={previewId}
            disabled={true}
            icon={Hash}
          />

          {selectedRole === 'mentor' && (
            <motion.div initial={{ opacity: 0, height: 0 }} animate={{ opacity: 1, height: 'auto' }}>
              <FormInput
                id="adminId"
                label="University Admin ID"
                type="text"
                placeholder="e.g. harsha21"
                error={errors.adminId?.message || customError}
                icon={Hash}
                {...register('adminId')}
              />
            </motion.div>
          )}

          <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '16px' }}>
            <FormInput
              id="firstName"
              label="First Name"
              type="text"
              placeholder="John"
              error={errors.firstName?.message}
              icon={User}
              {...register('firstName')}
            />
            <FormInput
              id="middleName"
              label="Middle Name"
              type="text"
              placeholder="Middle (Opt)"
              error={errors.middleName?.message}
              icon={User}
              {...register('middleName')}
            />
          </div>
          <FormInput
            id="lastName"
            label="Last Name"
            type="text"
            placeholder="Doe"
            error={errors.lastName?.message}
            icon={User}
            {...register('lastName')}
          />
          <FormInput
            id="dob"
            label="Date of Birth"
            type="date"
            error={errors.dob?.message}
            icon={Calendar}
            {...register('dob')}
          />
          <FormInput
            id="mobileNo"
            label="Mobile Number"
            type="tel"
            placeholder="+1 (555) 000-0000"
            error={errors.mobileNo?.message}
            icon={Phone}
            {...register('mobileNo')}
          />
          <FormInput
            id="email"
            label="Email Address"
            type="email"
            placeholder="your@email.com"
            error={errors.email?.message}
            icon={Mail}
            {...register('email')}
          />

          <FormInput
            id="password"
            label="Password"
            type="password"
            placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            error={errors.password?.message}
            icon={Lock}
            {...register('password')}
          />
          <FormInput
            id="confirmPassword"
            label="Confirm Password"
            type="password"
            placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
            error={errors.confirmPassword?.message}
            icon={Key}
            {...register('confirmPassword')}
          />

          {/* Terms Agreement */}
          <div style={{ marginBottom: '16px' }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
              <input
                type="checkbox"
                id="agreeToTerms"
                {...register('agreeToTerms')}
                style={{ cursor: 'pointer', accentColor: 'var(--primary)' }}
              />
              <label htmlFor="agreeToTerms" style={{ fontSize: '0.875rem', color: 'var(--text-main)', cursor: 'pointer' }}>
                I agree to the Terms of Service and Privacy Policy
              </label>
            </div>
            {errors.agreeToTerms && (
              <p style={{ color: 'var(--danger)', fontSize: '0.75rem', marginTop: '4px' }}>{errors.agreeToTerms.message}</p>
            )}
          </div>

          <Button type="submit" variant="primary" disabled={isSubmitting} style={{ width: '100%' }}>
            {isSubmitting ? 'Creating Account...' : 'Create Account'}
          </Button>
        </form>

        {/* Divider */}
        <div className={styles.divider}>
          <div className={styles.dividerLine} />
          <span className={styles.dividerText}>Already have an account?</span>
        </div>

        {/* Login Link */}
        <Button onClick={() => navigate('/login')} variant="secondary" style={{ width: '100%' }}>
          Sign In
        </Button>

        {/* Back to Landing */}
        <div className={styles.backLink}>
          <button onClick={() => navigate('/')} className={styles.backBtn}>
            â† Back to Home
          </button>
        </div>
      </motion.div>
    </div>
  );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, ChartCard, StatCard, Button, Table } from '../components';
import { BarChart, Bar, LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';
import { Download, Calendar, TrendingUp } from 'lucide-react';
import { useToast } from '../components/Toast';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export const ReportsPage = () => {
  const { addToast } = useToast();
  const [dateRange, setDateRange] = useState('month');

  const growthData = [
    { date: 'Jan 1', value: 10 },
    { date: 'Jan 8', value: 15 },
    { date: 'Jan 15', value: 25 },
    { date: 'Jan 22', value: 30 },
    { date: 'Jan 29', value: 45 },
    { date: 'Feb 5', value: 60 },
    { date: 'Feb 12', value: 75 },
  ];

  const categoryStats = [
    { id: 1, category: 'Sports', achievements: 35 },
    { id: 2, category: 'Technical', achievements: 28 },
    { id: 3, category: 'Cultural', achievements: 25 },
    { id: 4, category: 'Other', achievements: 12 },
  ];

  const statisticsData = [
    { label: 'Total Achievements', value: 100, trend: 12 },
    { label: 'New This Month', value: 18, trend: 8 },
    { label: 'Verified', value: 95, trend: 5 },
    { label: 'Pending', value: 5, trend: -2 },
  ];

  const columns = [
    { key: 'category', label: 'Category' },
    { key: 'achievements', label: 'Count' },
    {
      key: 'percentage',
      label: 'Percentage',
      render: (item) => <span style={{ color: 'var(--primary)' }}>{Math.round((item.achievements / 100) * 100)}%</span>
    },
    {
      key: 'trend',
      label: 'Trend',
      render: () => (
        <span style={{
          padding: '4px 12px',
          borderRadius: '9999px',
          fontSize: '0.75rem',
          fontWeight: '600',
          background: 'var(--success-bg)',
          color: 'var(--success)',
          border: '1px solid rgba(16, 185, 129, 0.3)'
        }}>
          â†‘ +5%
        </span>
      )
    },
  ];

  const handleDownloadReport = async () => {
    const reportElement = document.getElementById('report-content');
    if (!reportElement) return;

    addToast('Generating full PDF report...', 'info');
    try {
      // Temporarily hide the download button to prevent it from appearing in the PDF
      const downloadBtn = document.getElementById('download-report-btn');
      if (downloadBtn) downloadBtn.style.display = 'none';

      const canvas = await html2canvas(reportElement, {
        scale: 2,
        backgroundColor: '#151521' // Match the dark background
      });

      if (downloadBtn) downloadBtn.style.display = 'flex';

      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('p', 'mm', 'a4'); // Portrait A4

      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;

      let position = 0;
      let heightLeft = pdfHeight;
      const pageHeight = pdf.internal.pageSize.getHeight();

      // Handle multi-page PDF
      pdf.addImage(imgData, 'PNG', 0, position, pdfWidth, pdfHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - pdfHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, pdfWidth, pdfHeight);
        heightLeft -= pageHeight;
      }

      pdf.save(`SAAMS_Analytics_Report_${dateRange}.pdf`);
      addToast('Report downloaded successfully!', 'success');
    } catch (error) {
      console.error('Error generating PDF report:', error);
      addToast('Failed to generate PDF report.', 'error');
    }
  };

  return (
    <div id="report-content" style={{ flex: 1, padding: 'var(--spacing-4)', background: 'var(--bg-dark)' }}>
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', gap: 'var(--spacing-4)' }}>
          <div>
            <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>Reports</h1>
            <p style={{ color: 'var(--text-muted)', marginTop: 'var(--spacing-1)', margin: 0 }}>Comprehensive achievement analytics and statistics</p>
          </div>
          <Button
            id="download-report-btn"
            variant="primary"
            onClick={handleDownloadReport}
            style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)' }}
          >
            <Download size={16} />
            Download Report
          </Button>
        </div>
      </motion.div>

      {/* Date Range Selector */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <Card style={{ padding: 'var(--spacing-4)', display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
          <div>
            <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: 'var(--text-light)', display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', margin: '0 0 var(--spacing-4) 0' }}>
              <Calendar size={20} />
              Report Period
            </h3>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(100px, 1fr))', gap: 'var(--spacing-3)', marginBottom: 'var(--spacing-4)' }}>
              {['week', 'month', 'quarter', 'year'].map((period) => (
                <button
                  key={period}
                  onClick={() => setDateRange(period)}
                  style={{
                    padding: 'var(--spacing-2) var(--spacing-4)',
                    borderRadius: 'var(--radius-md)',
                    fontWeight: '600',
                    textTransform: 'capitalize',
                    transition: 'all var(--transition-normal)',
                    background: dateRange === period ? 'linear-gradient(135deg, var(--primary), var(--secondary))' : 'var(--bg-main)',
                    border: dateRange === period ? 'none' : '1px solid var(--card-border)',
                    color: dateRange === period ? 'white' : 'var(--text-muted)',
                    cursor: 'pointer',
                    boxShadow: dateRange === period ? 'var(--shadow-glow)' : 'none',
                  }}
                >
                  {period}
                </button>
              ))}
            </div>
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 'var(--spacing-3)' }}>
              <input type="date" style={{
                padding: 'var(--spacing-3)',
                borderRadius: 'var(--radius-md)',
                background: 'var(--bg-main)',
                border: '1px solid var(--card-border)',
                color: 'var(--text-light)',
                colorScheme: 'dark'
              }} />
              <input type="date" style={{
                padding: 'var(--spacing-3)',
                borderRadius: 'var(--radius-md)',
                background: 'var(--bg-main)',
                border: '1px solid var(--card-border)',
                color: 'var(--text-light)',
                colorScheme: 'dark'
              }} />
            </div>
          </div>
        </Card>
      </motion.div>

      {/* Statistics Cards */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ staggerChildren: 0.1 }}
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))',
          gap: 'var(--spacing-4)',
          marginBottom: 'var(--spacing-8)'
        }}
      >
        {statisticsData.map((stat, index) => (
          <motion.div
            key={index}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.05 }}
          >
            <StatCard
              label={stat.label}
              value={stat.value}
              trend={stat.trend}
              icon={TrendingUp}
              color="from-purple-500 to-indigo-600"
            />
          </motion.div>
        ))}
      </motion.div>

      {/* Charts */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ staggerChildren: 0.1 }}
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: 'var(--spacing-6)',
          marginBottom: 'var(--spacing-8)'
        }}
      >
        {/* Growth Chart */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <ChartCard title="Achievement Growth" subtitle="Trend over time">
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={growthData}>
                <CartesianGrid strokeDasharray="3 3" stroke="var(--card-border)" />
                <XAxis dataKey="date" stroke="var(--text-muted)" />
                <YAxis stroke="var(--text-muted)" />
                <Tooltip
                  contentStyle={{
                    backgroundColor: 'var(--bg-dark)',
                    border: '1px solid var(--card-border)',
                    borderRadius: 'var(--radius-md)',
                    color: 'var(--text-light)'
                  }}
                  itemStyle={{ color: 'var(--text-light)' }}
                />
                <Line
                  type="monotone"
                  dataKey="value"
                  stroke="var(--primary)"
                  strokeWidth={3}
                  dot={{ fill: 'var(--primary)' }}
                  activeDot={{ r: 6 }}
                />
              </LineChart>
            </ResponsiveContainer>
          </ChartCard>
        </motion.div>

        {/* Category Distribution Chart */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
        >
          <ChartCard title="Category Distribution" subtitle="By achievement type">
            <ResponsiveContainer width="100%" height={300}>
              <BarChart data={categoryStats}>
                <CartesianGrid strokeDasharray="3 3" stroke="var(--card-border)" />
                <XAxis dataKey="category" stroke="var(--text-muted)" />
                <YAxis stroke="var(--text-muted)" />
                <Tooltip
                  contentStyle={{
                    backgroundColor: 'var(--bg-dark)',
                    border: '1px solid var(--card-border)',
                    borderRadius: 'var(--radius-md)',
                    color: 'var(--text-light)'
                  }}
                  itemStyle={{ color: 'var(--text-light)' }}
                  cursor={{ fill: 'rgba(59, 130, 246, 0.1)' }}
                />
                <Bar dataKey="achievements" fill="var(--primary)" radius={[8, 8, 0, 0]} />
              </BarChart>
            </ResponsiveContainer>
          </ChartCard>
        </motion.div>
      </motion.div>

      {/* Summary Table */}
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}>
        <ChartCard title="Detailed Summary" subtitle="Complete achievement breakdown">
          <Table
            columns={columns}
            data={categoryStats}
            keyField="id"
          />
        </ChartCard>
      </motion.div>
    </div>
  );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, Button, FormInput } from '../components';
import { Bell, Moon, Globe, Shield, Save } from 'lucide-react';
import { useToast } from '../components/Toast';
import { useTheme } from '../utils/ThemeContext';

export const SettingsPage = () => {
  const { addToast } = useToast();
  const { theme, setTheme } = useTheme();
  const [settings, setLocalSettings] = useState({
    notifications: true,
    emailNotifications: true,
    twoFactor: false,
    language: 'en',
  });

  const handleToggle = (key) => {
    setLocalSettings({ ...settings, [key]: !settings[key] });
  };

  const handleSave = () => {
    addToast('Settings saved successfully!', 'success');
  };

  return (
    <div style={{ flex: 1, padding: 'var(--spacing-4)' }}>
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: '0 0 var(--spacing-2) 0' }}>Settings</h1>
        <p style={{ color: 'var(--text-muted)', margin: 0 }}>Customize your SAAMS experience</p>
      </motion.div>

      <div style={{ maxWidth: '48rem', marginTop: 'var(--spacing-8)', display: 'flex', flexDirection: 'column', gap: 'var(--spacing-6)' }}>
        {/* Notifications */}
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
          <Card style={{ padding: 'var(--spacing-6)' }}>
            <h2 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-light)', display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', margin: '0 0 var(--spacing-6) 0' }}>
              <Bell size={20} />
              Notifications
            </h2>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: 'var(--spacing-4)', background: 'var(--bg-surface)', border: '1px solid var(--border-default)', borderRadius: 'var(--radius-md)' }}>
                <div>
                  <p style={{ color: 'var(--text-light)', fontWeight: '600', margin: '0 0 var(--spacing-1) 0' }}>Push Notifications</p>
                  <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>Receive notifications on your device</p>
                </div>
                <input
                  type="checkbox"
                  checked={settings.notifications}
                  onChange={() => handleToggle('notifications')}
                  style={{ width: '20px', height: '20px', cursor: 'pointer' }}
                />
              </div>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: 'var(--spacing-4)', background: 'var(--bg-surface)', border: '1px solid var(--border-default)', borderRadius: 'var(--radius-md)' }}>
                <div>
                  <p style={{ color: 'var(--text-light)', fontWeight: '600', margin: '0 0 var(--spacing-1) 0' }}>Email Notifications</p>
                  <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>Get updates via email</p>
                </div>
                <input
                  type="checkbox"
                  checked={settings.emailNotifications}
                  onChange={() => handleToggle('emailNotifications')}
                  style={{ width: '20px', height: '20px', cursor: 'pointer' }}
                />
              </div>
            </div>
          </Card>
        </motion.div>

        {/* Appearance */}
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.1 }}>
          <Card style={{ padding: 'var(--spacing-6)' }}>
            <h2 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-light)', display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', margin: '0 0 var(--spacing-6) 0' }}>
              <Moon size={20} />
              Appearance
            </h2>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
              <div>
                <label style={{ display: 'block', color: 'var(--text-light)', fontWeight: '600', marginBottom: 'var(--spacing-3)' }}>Theme</label>
                <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: 'var(--spacing-3)' }}>
                  {['light', 'dark', 'auto'].map((t) => (
                    <motion.button
                      key={t}
                      whileHover={{ scale: 1.05 }}
                      onClick={() => setTheme(t)}
                      style={{
                        padding: 'var(--spacing-2) var(--spacing-3)',
                        borderRadius: 'var(--radius-md)',
                        fontWeight: '500',
                        transition: 'all var(--transition-normal)',
                        textTransform: 'capitalize',
                        background: theme === t ? 'linear-gradient(to right, var(--primary), var(--secondary))' : 'var(--bg-surface)',
                        border: theme === t ? 'none' : '1px solid var(--border-default)',
                        color: theme === t ? 'white' : 'var(--text-main)',
                        cursor: 'pointer'
                      }}
                    >
                      {t}
                    </motion.button>
                  ))}
                </div>
              </div>
              <div>
                <label style={{ display: 'block', color: 'var(--text-light)', fontWeight: '600', marginBottom: 'var(--spacing-2)' }}>Language</label>
                <select
                  value={settings.language}
                  onChange={(e) => setLocalSettings({ ...settings, language: e.target.value })}
                  style={{
                    width: '100%',
                    padding: 'var(--spacing-3)',
                    borderRadius: 'var(--radius-md)',
                    background: 'var(--bg-main)',
                    border: '1px solid var(--border-default)',
                    color: 'var(--text-main)'
                  }}
                >
                  <option value="en" style={{ background: 'var(--bg-elevated)' }}>English</option>
                  <option value="es" style={{ background: 'var(--bg-elevated)' }}>Spanish</option>
                  <option value="fr" style={{ background: 'var(--bg-elevated)' }}>French</option>
                  <option value="hi" style={{ background: 'var(--bg-elevated)' }}>Hindi</option>
                </select>
              </div>
            </div>
          </Card>
        </motion.div>

        {/* Privacy & Security */}
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ delay: 0.2 }}>
          <Card style={{ padding: 'var(--spacing-6)' }}>
            <h2 style={{ fontSize: '1.25rem', fontWeight: '700', color: 'var(--text-light)', display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', margin: '0 0 var(--spacing-6) 0' }}>
              <Shield size={20} />
              Privacy & Security
            </h2>
            <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
              <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: 'var(--spacing-4)', background: 'var(--bg-surface)', border: '1px solid var(--border-default)', borderRadius: 'var(--radius-md)' }}>
                <div>
                  <p style={{ color: 'var(--text-light)', fontWeight: '600', margin: '0 0 var(--spacing-1) 0' }}>Two-Factor Authentication</p>
                  <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>Add extra security to your account</p>
                </div>
                <input
                  type="checkbox"
                  checked={settings.twoFactor}
                  onChange={() => handleToggle('twoFactor')}
                  style={{ width: '20px', height: '20px', cursor: 'pointer' }}
                />
              </div>
              <Button
                variant="secondary"
                onClick={() => addToast('Opening Privacy Policy...', 'info')}
                style={{ width: '100%' }}
              >
                View Privacy Policy
              </Button>
              <Button
                variant="secondary"
                onClick={() => addToast('Opening Terms of Service...', 'info')}
                style={{ width: '100%' }}
              >
                Terms of Service
              </Button>
            </div>
          </Card>
        </motion.div>

        {/* Save Button */}
        <Button
          variant="primary"
          onClick={handleSave}
          style={{ width: '100%', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: 'var(--spacing-2)' }}
        >
          <Save size={16} />
          Save All Settings
        </Button>
      </div>
    </div>
  );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { StatCard, ChartCard, Card, Button } from '../components';
import { PieChart, Pie, Cell, ResponsiveContainer, Tooltip } from 'recharts';
import { mockStudents, mockAchievements, categoryDistributionData } from '../data/mockData';
import { Award, Filter, Download, Calendar, Plus } from 'lucide-react';
import { useToast } from '../components/Toast';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../utils/AuthContext';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export const StudentDashboard = () => {
  const [selectedFilter, setSelectedFilter] = useState('all');
  const { addToast } = useToast();
  const navigate = useNavigate();
  const { user } = useAuth();

  const student = mockStudents[0]; // Get first student as example (keep for mock stats)
  const studentAchievements = mockAchievements.slice(0, 3); // Mock achievements for student

  const COLORS = ['var(--primary)', 'var(--secondary)', 'var(--warning)', 'var(--success)'];

  const stats = [
    { label: 'Total Achievements', value: student.totalAchievements, icon: Award, color: 'from-purple-500 to-indigo-600' },
  ];

  const badges = [
    { name: 'Rising Star', description: '5+ Achievements' },
    { name: 'Team Player', description: 'Sports Category' },
    { name: 'Tech Master', description: 'Technical Excellence' },
  ];

  const handleDownloadCertificate = async (achievement) => {
    addToast(`Generating certificate for ${achievement.activity}...`, 'info');

    // Create a temporary hidden div to render the certificate
    const tempDiv = document.createElement('div');
    tempDiv.style.position = 'absolute';
    tempDiv.style.left = '-9999px';
    tempDiv.style.top = '-9999px';
    document.body.appendChild(tempDiv);

    // Simple HTML structure for the certificate
    tempDiv.innerHTML = `
      <div id="temp-cert" style="
        width: 800px;
        height: 565px;
        background: #1e1e2d;
        color: white;
        padding: 40px;
        font-family: sans-serif;
        text-align: center;
        border: 2px solid #3b82f6;
        border-radius: 8px;
        position: relative;
        box-sizing: border-box;
      ">
        <div style="font-size: 60px; margin-bottom: 20px;">ðŸŽ“</div>
        <h2 style="font-size: 36px; margin: 0 0 20px 0; color: #fff;">Certificate of Achievement</h2>
        <p style="color: #94a3b8; font-size: 18px; margin: 0 0 10px 0;">This is to certify that</p>
        <h3 style="font-size: 28px; color: #3b82f6; margin: 0 0 30px 0;">${achievement.studentName || student.name}</h3>
        <p style="color: #94a3b8; font-size: 18px; margin: 0 0 10px 0;">has successfully achieved</p>
        <h4 style="font-size: 24px; color: #fff; margin: 0 0 30px 0;">${achievement.position} in ${achievement.activity}</h4>
        <div style="margin-top: 20px;">
          <span style="padding: 4px 12px; background: #151521; border: 1px solid #2a2a3c; border-radius: 20px; font-size: 14px; margin-right: 10px;">${achievement.category}</span>
          <span style="padding: 4px 12px; background: #151521; border: 1px solid #2a2a3c; border-radius: 20px; font-size: 14px;">${achievement.level} Level</span>
        </div>
        <div style="position: absolute; bottom: 40px; right: 40px; text-align: right;">
          <p style="color: #94a3b8; font-size: 14px; margin: 0 0 5px 0;">Date Awarded</p>
          <p style="color: #fff; font-size: 16px; font-weight: bold; margin: 0;">${new Date(achievement.date).toLocaleDateString()}</p>
        </div>
      </div>
    `;

    try {
      const certElement = document.getElementById('temp-cert');
      const canvas = await html2canvas(certElement, { scale: 2 });
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('l', 'mm', 'a4');

      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;

      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save(`${achievement.studentName || student.name}_${achievement.activity}_Certificate.pdf`);
      addToast('Certificate downloaded successfully!', 'success');
    } catch (error) {
      console.error('Error generating PDF:', error);
      addToast('Failed to generate PDF.', 'error');
    } finally {
      document.body.removeChild(tempDiv);
    }
  };

  return (
    <div style={{ flex: 1, padding: 'var(--spacing-4)' }}>
      {/* Welcome Section */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <div style={{
          background: 'var(--bg-elevated)',
          border: '1px solid var(--border-default)',
          borderRadius: 'var(--radius-xl)',
          padding: 'var(--spacing-6)'
        }}>
          <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', gap: 'var(--spacing-4)' }}>
            <div>
              <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>Welcome, {user?.name || student.name}!</h1>
              <p style={{ color: 'var(--text-muted)', marginTop: 'var(--spacing-1)', margin: 0 }}>Track and celebrate your achievements</p>
            </div>
            <div style={{ fontSize: '2.25rem' }}>ðŸŽ“</div>
          </div>
        </div>
      </motion.div>

      {/* Statistics */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))', gap: 'var(--spacing-4)' }}>
          {stats.map((stat, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
            >
              <StatCard
                icon={stat.icon}
                label={stat.label}
                value={stat.value}
                color={stat.color}
              />
            </motion.div>
          ))}
        </div>
      </motion.div>

      {/* Main Grid */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ staggerChildren: 0.1 }}
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: 'var(--spacing-8)',
          marginBottom: 'var(--spacing-8)'
        }}
      >
        {/* Category Distribution */}
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}>
          <ChartCard title="Category Breakdown" subtitle="Your achievements by type">
            <ResponsiveContainer width="100%" height={250}>
              <PieChart>
                <Pie
                  data={categoryDistributionData}
                  cx="50%"
                  cy="50%"
                  labelLine={false}
                  label={({ name }) => <text fill="var(--text-light)" fontSize="12" dy={-10}>{name}</text>}
                  outerRadius={70}
                  fill="#8884d8"
                  dataKey="value"
                  stroke="var(--bg-dark)"
                  strokeWidth={2}
                >
                  {categoryDistributionData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip
                  contentStyle={{
                    backgroundColor: 'var(--bg-dark)',
                    border: '1px solid var(--card-border)',
                    borderRadius: 'var(--radius-md)',
                    color: 'var(--text-light)'
                  }}
                  itemStyle={{ color: 'var(--text-light)' }}
                />
              </PieChart>
            </ResponsiveContainer>
          </ChartCard>
        </motion.div>

        {/* Badges & Achievements */}
        <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} style={{ gridColumn: '1 / -1', '@media (min-width: 1024px)': { gridColumn: 'span 2' } }}>
          <ChartCard title="Achievement Badges" subtitle="Your earned recognition">
            <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-3)' }}>
              {badges.map((badge, index) => (
                <motion.div
                  key={index}
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  transition={{ delay: index * 0.1 }}
                  style={{
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'space-between',
                    padding: 'var(--spacing-4)',
                    background: 'var(--bg-main)',
                    border: '1px solid var(--border-default)',
                    borderRadius: 'var(--radius-xl)',
                    cursor: 'pointer',
                    transition: 'border-color var(--transition-normal)'
                  }}
                  whileHover={{ borderColor: 'var(--primary)' }}
                >
                  <div style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-3)' }}>
                    <div style={{ fontSize: '1.5rem' }}>ðŸ†</div>
                    <div>
                      <p style={{ fontWeight: '600', color: 'var(--text-light)', margin: 0 }}>{badge.name}</p>
                      <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', margin: 0 }}>{badge.description}</p>
                    </div>
                  </div>
                  <span style={{
                    padding: '2px 8px',
                    borderRadius: '9999px',
                    fontSize: '0.75rem',
                    background: 'var(--success-bg)',
                    color: 'var(--success)',
                    border: '1px solid var(--success)'
                  }}>
                    Earned
                  </span>
                </motion.div>
              ))}
            </div>
          </ChartCard>
        </motion.div>
      </motion.div>

      {/* Timeline & Recent Achievements */}
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }}>
        <div style={{ marginBottom: 'var(--spacing-6)' }}>
          <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', gap: 'var(--spacing-4)', marginBottom: 'var(--spacing-4)' }}>
            <div>
              <h2 style={{ fontSize: '1.5rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>My Achievements</h2>
              <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>Timeline of your accomplishments</p>
            </div>
            <div style={{ display: 'flex', gap: 'var(--spacing-2)' }}>
              <Button
                variant="primary"
                onClick={() => navigate('/student/add-achievement')}
                style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', fontSize: '0.875rem' }}
              >
                <Plus size={16} />
                Add
              </Button>
              <Button
                variant="secondary"
                onClick={() => addToast('Exporting timeline...', 'info')}
                style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', fontSize: '0.875rem' }}
              >
                <Download size={16} />
                Export
              </Button>
            </div>
          </div>

          {/* Timeline */}
          <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
            {studentAchievements.map((achievement, index) => (
              <motion.div
                key={achievement.id}
                initial={{ opacity: 0, x: -20 }}
                animate={{ opacity: 1, x: 0 }}
                transition={{ delay: index * 0.1 }}
              >
                <Card style={{ padding: 'var(--spacing-4)' }}>
                  <div style={{ display: 'flex', gap: 'var(--spacing-4)' }}>
                    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
                      <div style={{ width: '16px', height: '16px', borderRadius: '50%', background: 'linear-gradient(to right, var(--primary), var(--secondary))', marginTop: '8px' }} />
                      {index < studentAchievements.length - 1 && (
                        <div style={{ width: '2px', height: '64px', background: 'linear-gradient(to bottom, var(--primary), transparent)', marginTop: '8px' }} />
                      )}
                    </div>
                    <div style={{ flex: 1, paddingBottom: 'var(--spacing-4)' }}>
                      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', gap: 'var(--spacing-2)', marginBottom: 'var(--spacing-2)' }}>
                        <h3 style={{ fontWeight: '600', color: 'var(--text-light)', fontSize: '1.125rem', margin: 0 }}>{achievement.activity}</h3>
                        <div style={{ display: 'flex', gap: 'var(--spacing-2)' }}>
                          <span style={{ padding: '2px 8px', borderRadius: '9999px', fontSize: '0.75rem', background: 'var(--bg-surface)', color: 'var(--text-main)', border: '1px solid var(--border-default)' }}>{achievement.level}</span>
                          <span style={{ padding: '2px 8px', borderRadius: '9999px', fontSize: '0.75rem', background: 'var(--success-bg)', color: 'var(--success)', border: '1px solid var(--success)' }}>{achievement.status}</span>
                        </div>
                      </div>
                      <p style={{ fontSize: '0.875rem', color: 'var(--text-light)', opacity: 0.8, margin: '0 0 var(--spacing-2) 0' }}>{achievement.category} â€¢ {achievement.position}</p>
                      <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', margin: 0 }}>ðŸ“… {new Date(achievement.date).toLocaleDateString()}</p>
                    </div>
                  </div>
                </Card>
              </motion.div>
            ))}
          </div>
        </div>
      </motion.div>

      {/* Certificates Section */}
      <motion.div initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} style={{ marginTop: 'var(--spacing-8)' }}>
        <div style={{ marginBottom: 'var(--spacing-4)' }}>
          <h2 style={{ fontSize: '1.5rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>Certificates</h2>
          <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>View and download your achievement certificates</p>
        </div>

        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 'var(--spacing-4)' }}>
          {studentAchievements.map((achievement, index) => (
            <motion.div
              key={achievement.id}
              initial={{ opacity: 0, scale: 0.95 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ delay: index * 0.1 }}
            >
              <Card hoverable style={{ overflow: 'hidden', padding: 'var(--spacing-4)' }}>
                <div style={{
                  aspectRatio: '16/9',
                  background: 'linear-gradient(to bottom right, var(--primary), var(--secondary))',
                  borderRadius: 'var(--radius-md)',
                  marginBottom: 'var(--spacing-4)',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center'
                }}>
                  <span style={{ fontSize: '1.875rem' }}>ðŸ“œ</span>
                </div>
                <h3 style={{ fontWeight: '600', color: 'var(--text-light)', overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap', margin: '0 0 var(--spacing-1) 0' }}>{achievement.activity}</h3>
                <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', margin: '0 0 var(--spacing-4) 0' }}>{new Date(achievement.date).toLocaleDateString()}</p>
                <Button
                  variant="secondary"
                  onClick={() => handleDownloadCertificate(achievement)}
                  style={{ width: '100%', fontSize: '0.875rem' }}
                >
                  Download
                </Button>
              </Card>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </div>
  );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, FormInput, Button } from '../components';
import { mockStudents } from '../data/mockData';
import { Search, Filter, Mail, Phone } from 'lucide-react';
import { useToast } from '../components/Toast';

export const StudentsPage = () => {
  const { addToast } = useToast();
  const [searchQuery, setSearchQuery] = useState('');
  const [sortBy, setSortBy] = useState('name');

  const filteredStudents = mockStudents.filter((student) =>
    student.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.studentId.toLowerCase().includes(searchQuery.toLowerCase()) ||
    student.email.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const sortedStudents = [...filteredStudents].sort((a, b) => {
    switch (sortBy) {
      case 'achievements':
        return b.totalAchievements - a.totalAchievements;
      case 'id':
        return a.studentId.localeCompare(b.studentId);
      default:
        return a.name.localeCompare(b.name);
    }
  });

  return (
    <div style={{ flex: 1, padding: 'var(--spacing-4)' }}>
      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>Students</h1>
        <p style={{ color: 'var(--text-muted)', marginTop: 'var(--spacing-1)', margin: 0 }}>Manage and view student profiles</p>
      </motion.div>

      {/* Search and Filter */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <Card style={{ padding: 'var(--spacing-4)', display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
          <div>
            <FormInput
              type="text"
              placeholder="Search by name, ID, or email..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              icon={Search}
              id="studentSearch"
            />
          </div>

          <div style={{ display: 'flex', flexWrap: 'wrap', gap: 'var(--spacing-4)' }}>
            <div style={{ flex: '1 1 200px' }}>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '600', color: 'var(--text-light)', marginBottom: 'var(--spacing-2)' }}>Sort By</label>
              <select
                value={sortBy}
                onChange={(e) => setSortBy(e.target.value)}
                style={{
                  width: '100%',
                  padding: 'var(--spacing-3)',
                  borderRadius: 'var(--radius-md)',
                  background: 'var(--bg-main)',
                  border: '1px solid var(--border-default)',
                  color: 'var(--text-main)'
                }}
              >
                <option value="name" style={{ background: 'var(--bg-elevated)' }}>Name (A-Z)</option>
                <option value="id" style={{ background: 'var(--bg-elevated)' }}>Student ID</option>
                <option value="achievements" style={{ background: 'var(--bg-elevated)' }}>Total Achievements</option>
              </select>
            </div>
            <div style={{ flex: '1 1 200px' }}>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '600', color: 'var(--text-light)', marginBottom: 'var(--spacing-2)' }}>Results</label>
              <div style={{
                background: 'var(--bg-surface)',
                border: '1px solid var(--border-default)',
                borderRadius: 'var(--radius-md)',
                padding: 'var(--spacing-3)',
                color: 'var(--text-main)'
              }}>
                {sortedStudents.length} students found
              </div>
            </div>
          </div>
        </Card>
      </motion.div>

      {/* Students Grid */}
      {sortedStudents.length > 0 ? (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ staggerChildren: 0.05 }}
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))',
            gap: 'var(--spacing-6)'
          }}
        >
          {sortedStudents.map((student, index) => (
            <motion.div
              key={student.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.05 }}
            >
              <Card hoverable style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)', height: '100%' }}>
                {/* Student Image */}
                <div style={{ display: 'flex', justifyContent: 'center' }}>
                  <img
                    src={student.image}
                    alt={student.name}
                    style={{
                      width: '96px',
                      height: '96px',
                      borderRadius: '50%',
                      objectFit: 'cover',
                      border: '4px solid var(--border-default)'
                    }}
                  />
                </div>

                {/* Student Info */}
                <div style={{ textAlign: 'center', display: 'flex', flexDirection: 'column', gap: 'var(--spacing-2)' }}>
                  <h3 style={{ fontSize: '1.125rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>{student.name}</h3>
                  <p style={{ fontSize: '0.875rem', color: 'var(--text-muted)', margin: 0 }}>ID: {student.studentId}</p>
                  <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '4px', fontSize: '0.75rem', color: 'var(--text-main)' }}>
                    <Mail size={16} />
                    <span style={{ overflow: 'hidden', textOverflow: 'ellipsis', whiteSpace: 'nowrap' }}>{student.email}</span>
                  </div>
                </div>

                {/* Achievements */}
                <div style={{
                  background: 'var(--bg-surface)',
                  border: '1px solid var(--border-default)',
                  borderRadius: 'var(--radius-md)',
                  padding: 'var(--spacing-3)',
                  textAlign: 'center'
                }}>
                  <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', margin: '0 0 var(--spacing-2) 0' }}>Total Achievements</p>
                  <p style={{ fontSize: '1.5rem', fontWeight: '700', color: 'var(--text-main)', margin: 0 }}>{student.totalAchievements}</p>
                </div>

                {/* Category Breakdown */}
                <div style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-2)' }}>
                  <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', fontWeight: '600', margin: 0 }}>Categories</p>
                  <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 'var(--spacing-2)' }}>
                    {Object.entries(student.categories).map(([category, count]) => (
                      <div key={category} style={{ background: 'var(--bg-surface)', borderRadius: 'var(--radius-md)', padding: 'var(--spacing-2)', border: '1px solid var(--border-default)', textAlign: 'center' }}>
                        <p style={{ fontSize: '0.75rem', color: 'var(--text-muted)', margin: 0 }}>{category}</p>
                        <p style={{ fontSize: '1.125rem', fontWeight: '700', color: 'var(--text-main)', margin: 0 }}>{count}</p>
                      </div>
                    ))}
                  </div>
                </div>

                {/* View Button */}
                <div style={{ marginTop: 'auto', paddingTop: 'var(--spacing-4)' }}>
                  <Button
                    variant="secondary"
                    onClick={() => addToast(`Opening profile for ${student.name}...`, 'info')}
                    style={{ width: '100%' }}
                  >
                    View Profile
                  </Button>
                </div>
              </Card>
            </motion.div>
          ))}
        </motion.div>
      ) : (
        <Card style={{ textAlign: 'center', padding: 'var(--spacing-12)' }}>
          <p style={{ color: 'var(--text-muted)', fontSize: '1.125rem', margin: 0 }}>No students found</p>
          <p style={{ color: 'var(--text-muted)', opacity: 0.7, fontSize: '0.875rem', marginTop: 'var(--spacing-2)', margin: 0 }}>Try adjusting your search query</p>
        </Card>
      )}
    </div>
  );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, FormInput, Button, Table } from '../components';
import { useAuth } from '../utils/AuthContext';
import { useToast } from '../components/Toast';
import { UserPlus, Shield, Buildings } from 'lucide-react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import * as z from 'zod';
import { generateStudentId } from '../utils/idGenerator';

const adminSchema = z.object({
    id: z.string().min(1, 'Admin ID/Username is required'),
    name: z.string().min(1, 'University/Admin Name is required'),
    email: z.string().email('Invalid email format'),
    password: z.string().min(6, 'Password must be at least 6 characters'),
});

export const SuperAdminDashboard = () => {
    const { user, usersDB, createUniversityAdmin } = useAuth();
    const { addToast } = useToast();
    const [isCreating, setIsCreating] = useState(false);

    // Filter out the admins
    const universityAdmins = usersDB.filter(u => u.role === 'admin');

    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm({
        resolver: zodResolver(adminSchema),
        defaultValues: {
            id: '',
            name: '',
            email: '',
            password: '',
        }
    });

    const onSubmit = (data) => {
        setIsCreating(true);
        try {
            createUniversityAdmin(data);
            addToast(`University Admin created successfully!`, 'success');
            reset(); // Clear the form
        } catch (error) {
            addToast(error.message, 'error');
        }
        setIsCreating(false);
    };

    const columns = [
        { key: 'id', label: 'Admin ID / University Code' },
        { key: 'name', label: 'University Name' },
        { key: 'email', label: 'Admin Email' },
        { key: 'createdAt', label: 'Created At', render: (item) => new Date(item.createdAt).toLocaleDateString() },
    ];

    return (
        <div style={{ flex: 1, padding: 'var(--spacing-4)' }}>
            {/* Header */}
            <motion.div
                initial={{ opacity: 0, y: -20 }}
                animate={{ opacity: 1, y: 0 }}
                style={{ marginBottom: 'var(--spacing-8)' }}
            >
                <div style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-4)' }}>
                    <div style={{ padding: 'var(--spacing-3)', background: 'rgba(59, 130, 246, 0.1)', borderRadius: 'var(--radius-lg)' }}>
                        <Shield size={32} color="var(--primary)" />
                    </div>
                    <div>
                        <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>Super Admin Hub</h1>
                        <p style={{ color: 'var(--text-muted)', marginTop: 'var(--spacing-1)', margin: 0 }}>Create and manage University Administrators</p>
                    </div>
                </div>
            </motion.div>

            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: 'var(--spacing-8)' }}>

                {/* Create New Admin Form */}
                <motion.div
                    initial={{ opacity: 0, x: -20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.1 }}
                >
                    <Card style={{ padding: 'var(--spacing-6)' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-3)', marginBottom: 'var(--spacing-6)' }}>
                            <UserPlus size={24} color="var(--text-light)" />
                            <h2 style={{ fontSize: '1.25rem', fontWeight: '600', color: 'var(--text-light)', margin: 0 }}>Create University Admin</h2>
                        </div>

                        <form onSubmit={handleSubmit(onSubmit)} style={{ display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
                            <FormInput
                                label="Admin ID (University Code)"
                                type="text"
                                placeholder="e.g. nyu2024"
                                error={errors.id?.message}
                                {...register('id')}
                            />
                            <FormInput
                                label="University / Admin Name"
                                type="text"
                                placeholder="e.g. New York University"
                                error={errors.name?.message}
                                {...register('name')}
                            />
                            <FormInput
                                label="Admin Email"
                                type="email"
                                placeholder="admin@university.edu"
                                error={errors.email?.message}
                                {...register('email')}
                            />
                            <FormInput
                                label="Initial Password"
                                type="password"
                                placeholder="â€¢â€¢â€¢â€¢â€¢â€¢â€¢â€¢"
                                error={errors.password?.message}
                                {...register('password')}
                            />
                            <Button type="submit" variant="primary" disabled={isCreating} style={{ marginTop: 'var(--spacing-4)' }}>
                                {isCreating ? 'Creating...' : 'Create Admin Account'}
                            </Button>
                        </form>
                    </Card>
                </motion.div>

                {/* Active Admins List */}
                <motion.div
                    initial={{ opacity: 0, x: 20 }}
                    animate={{ opacity: 1, x: 0 }}
                    transition={{ delay: 0.2 }}
                >
                    <Card style={{ padding: 'var(--spacing-6)' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-3)', marginBottom: 'var(--spacing-6)' }}>
                            <Buildings size={24} color="var(--text-light)" />
                            <h2 style={{ fontSize: '1.25rem', fontWeight: '600', color: 'var(--text-light)', margin: 0 }}>Active Universities</h2>
                        </div>

                        {universityAdmins.length > 0 ? (
                            <Table
                                columns={columns}
                                data={universityAdmins}
                                keyField="id"
                            />
                        ) : (
                            <div style={{ padding: 'var(--spacing-8)', textAlign: 'center', border: '1px dashed var(--card-border)', borderRadius: 'var(--radius-lg)' }}>
                                <p style={{ color: 'var(--text-muted)', margin: 0 }}>No University Admins created yet.</p>
                            </div>
                        )}
                    </Card>
                </motion.div>

            </div>
        </div>
    );
};

import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { Card, AchievementCard, Button, FormInput, Modal } from '../components';
import { mockAchievements } from '../data/mockData';
import { Search, Filter, Download } from 'lucide-react';
import { useAuth } from '../utils/AuthContext';
import { useToast } from '../components/Toast';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';

export const ViewAchievementsPage = () => {
  const { user } = useAuth();
  const { addToast } = useToast();
  const isAdmin = user?.role === 'admin';

  const [searchQuery, setSearchQuery] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedLevel, setSelectedLevel] = useState('');
  const [selectedAchievement, setSelectedAchievement] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 6;

  const categories = ['Sports', 'Technical', 'Cultural', 'Other'];
  const levels = ['Participation', 'College', 'State', 'National', 'International'];

  // Mock data tailored for the student view
  const baseAchievements = isAdmin ? mockAchievements : mockAchievements.slice(0, 3);

  const filteredAchievements = baseAchievements.filter((achievement) => {
    const matchesSearch =
      achievement.studentName.toLowerCase().includes(searchQuery.toLowerCase()) ||
      achievement.activity.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesCategory = selectedCategory === '' || achievement.category === selectedCategory;
    const matchesLevel = selectedLevel === '' || achievement.level === selectedLevel;
    return matchesSearch && matchesCategory && matchesLevel;
  });

  // Calculate pagination
  const totalPages = Math.ceil(filteredAchievements.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const paginatedAchievements = filteredAchievements.slice(startIndex, startIndex + itemsPerPage);

  // Reset page when filters change
  React.useEffect(() => {
    setCurrentPage(1);
  }, [searchQuery, selectedCategory, selectedLevel]);

  const handleDownloadPDF = async () => {
    const certificateElement = document.getElementById('certificate-content');
    if (!certificateElement) return;

    addToast('Generating PDF certificate...', 'info');
    try {
      const canvas = await html2canvas(certificateElement, { scale: 2 });
      const imgData = canvas.toDataURL('image/png');
      const pdf = new jsPDF('l', 'mm', 'a4'); // Landscape A4

      const pdfWidth = pdf.internal.pageSize.getWidth();
      const pdfHeight = (canvas.height * pdfWidth) / canvas.width;

      pdf.addImage(imgData, 'PNG', 0, 0, pdfWidth, pdfHeight);
      pdf.save(`${selectedAchievement.studentName}_${selectedAchievement.activity}_Certificate.pdf`);
      addToast('Certificate downloaded successfully!', 'success');
    } catch (error) {
      console.error('Error generating PDF:', error);
      addToast('Failed to generate PDF.', 'error');
    }
  };

  return (
    <div style={{ flex: 1, padding: 'var(--spacing-4)' }}>
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center', gap: 'var(--spacing-4)' }}>
          <div>
            <h1 style={{ fontSize: '1.875rem', fontWeight: '700', color: 'var(--text-light)', margin: 0 }}>
              {isAdmin ? 'View Achievements' : 'My Achievements'}
            </h1>
            <p style={{ color: 'var(--text-muted)', marginTop: 'var(--spacing-1)', margin: 0 }}>
              {isAdmin ? 'Browse all student achievements' : 'Browse your recorded achievements'}
            </p>
          </div>
          <Button
            variant="secondary"
            onClick={() => addToast('Exporting achievements...', 'info')}
            style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)' }}
          >
            <Download size={16} />
            Export
          </Button>
        </div >
      </motion.div >

      {/* Filters */}
      < motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ marginBottom: 'var(--spacing-8)' }}
      >
        <Card style={{ padding: 'var(--spacing-4)', display: 'flex', flexDirection: 'column', gap: 'var(--spacing-4)' }}>
          <h3 style={{ fontSize: '1.125rem', fontWeight: '600', color: 'var(--text-light)', display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)', margin: '0 0 var(--spacing-4) 0' }}>
            <Filter size={20} />
            Filters
          </h3>

          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 'var(--spacing-4)', alignItems: 'end' }}>
            {/* Search (Only for Admin to search students, or search by activity) */}
            <div style={{ gridColumn: '1 / -1' }}>
              <FormInput
                type="text"
                placeholder={isAdmin ? "Search by student name or activity..." : "Search by activity name..."}
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                icon={Search}
                id="searchAchievements"
              />
            </div>

            {/* Category Filter */}
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '600', color: 'var(--text-light)', marginBottom: 'var(--spacing-2)' }}>Category</label>
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                style={{
                  width: '100%',
                  padding: 'var(--spacing-3)',
                  borderRadius: 'var(--radius-md)',
                  background: 'var(--bg-main)',
                  border: '1px solid var(--card-border)',
                  color: 'var(--text-light)'
                }}
              >
                <option value="" style={{ background: 'var(--bg-dark)' }}>All Categories</option>
                {categories.map((cat) => (
                  <option key={cat} value={cat} style={{ background: 'var(--bg-dark)' }}>
                    {cat}
                  </option>
                ))}
              </select>
            </div>

            {/* Level Filter */}
            <div>
              <label style={{ display: 'block', fontSize: '0.875rem', fontWeight: '600', color: 'var(--text-light)', marginBottom: 'var(--spacing-2)' }}>Level</label>
              <select
                value={selectedLevel}
                onChange={(e) => setSelectedLevel(e.target.value)}
                style={{
                  width: '100%',
                  padding: 'var(--spacing-3)',
                  borderRadius: 'var(--radius-md)',
                  background: 'var(--bg-main)',
                  border: '1px solid var(--card-border)',
                  color: 'var(--text-light)'
                }}
              >
                <option value="" style={{ background: 'var(--bg-dark)' }}>All Levels</option>
                {levels.map((level) => (
                  <option key={level} value={level} style={{ background: 'var(--bg-dark)' }}>
                    {level}
                  </option>
                ))}
              </select>
            </div>

            {/* Reset Button */}
            <div>
              <Button
                variant="secondary"
                onClick={() => {
                  setSearchQuery('');
                  setSelectedCategory('');
                  setSelectedLevel('');
                }}
                style={{ width: '100%' }}
              >
                Reset Filters
              </Button>
            </div>
          </div>
        </Card>
      </motion.div >

      {/* Results Info */}
      < motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        style={{ marginBottom: 'var(--spacing-4)' }}
      >
        <p style={{ color: 'var(--text-muted)', margin: 0 }}>
          Showing <span style={{ color: 'var(--text-light)', fontWeight: '600' }}>{paginatedAchievements.length}</span> of <span style={{ color: 'var(--text-light)', fontWeight: '600' }}>{filteredAchievements.length}</span> achievements
        </p>
      </motion.div >

      {/* Achievements List - Grid Layout */}
      < motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ staggerChildren: 0.05 }}
        style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
          gap: 'var(--spacing-6)'
        }}
      >
        {
          paginatedAchievements.length > 0 ? (
            paginatedAchievements.map((achievement, index) => (
              <motion.div
                key={achievement.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.05 }}
                onClick={() => setSelectedAchievement(achievement)}
                style={{ cursor: 'pointer', height: '100%' }}
              >
                <AchievementCard achievement={achievement} style={{ height: '100%' }} />
              </motion.div>
            ))
          ) : (
            <div style={{ gridColumn: '1 / -1' }}>
              <Card style={{ textAlign: 'center', padding: 'var(--spacing-12)' }}>
                <p style={{ color: 'var(--text-muted)', fontSize: '1.125rem', margin: 0 }}>No achievements found</p>
                <p style={{ color: 'var(--text-muted)', opacity: 0.7, fontSize: '0.875rem', marginTop: 'var(--spacing-2)', margin: 0 }}>Try adjusting your filters</p>
              </Card>
            </div>
          )
        }
      </motion.div >

      {/* Pagination Controls */}
      {totalPages > 1 && (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', gap: 'var(--spacing-4)', marginTop: 'var(--spacing-8)' }}>
          <Button
            variant="secondary"
            disabled={currentPage === 1}
            onClick={() => setCurrentPage(p => p - 1)}
          >
            Previous
          </Button>
          <span style={{ color: 'var(--text-light)', fontSize: '0.875rem' }}>
            Page {currentPage} of {totalPages}
          </span>
          <Button
            variant="secondary"
            disabled={currentPage === totalPages}
            onClick={() => setCurrentPage(p => p + 1)}
          >
            Next
          </Button>
        </div>
      )}

      {/* Certificate Modal */}
      < Modal
        isOpen={!!selectedAchievement}
        onClose={() => setSelectedAchievement(null)}
        title="Achievement Certificate"
        size="lg"
      >
        {selectedAchievement && (
          <div style={{ textAlign: 'center', padding: 'var(--spacing-4)' }}>
            <div id="certificate-content" style={{
              width: '100%',
              aspectRatio: '1.414', /* A4 Ratio */
              background: 'var(--bg-surface)',
              border: '1px solid var(--card-border)',
              borderRadius: 'var(--radius-md)',
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              padding: 'var(--spacing-8)',
              position: 'relative',
              overflow: 'hidden'
            }}>
              {/* Decorative Elements */}
              <div style={{ position: 'absolute', top: 0, left: 0, right: 0, height: '8px', background: 'linear-gradient(to right, var(--primary), var(--secondary))' }} />

              <div style={{ fontSize: '4rem', marginBottom: 'var(--spacing-4)' }}>ðŸŽ“</div>

              <h2 style={{ fontSize: '2rem', color: 'var(--text-light)', margin: '0 0 var(--spacing-6) 0', fontFamily: 'serif' }}>Certificate of Achievement</h2>

              <p style={{ color: 'var(--text-muted)', margin: '0 0 var(--spacing-2) 0' }}>This is to certify that</p>
              <h3 style={{ fontSize: '1.5rem', color: 'var(--primary)', margin: '0 0 var(--spacing-6) 0' }}>{selectedAchievement.studentName}</h3>

              <p style={{ color: 'var(--text-muted)', margin: '0 0 var(--spacing-2) 0' }}>has successfully achieved</p>
              <h4 style={{ fontSize: '1.25rem', color: 'var(--text-light)', margin: '0 0 var(--spacing-6) 0' }}>
                {selectedAchievement.position} in {selectedAchievement.activity}
              </h4>

              <div style={{ display: 'flex', gap: 'var(--spacing-4)', marginTop: 'var(--spacing-4)' }}>
                <span style={{ padding: '4px 12px', background: 'var(--bg-main)', border: '1px solid var(--border-default)', borderRadius: '9999px', fontSize: '0.875rem', color: 'var(--text-main)' }}>{selectedAchievement.category}</span>
                <span style={{ padding: '4px 12px', background: 'var(--bg-main)', border: '1px solid var(--border-default)', borderRadius: '9999px', fontSize: '0.875rem', color: 'var(--text-main)' }}>{selectedAchievement.level} Level</span>
              </div>

              <div style={{ position: 'absolute', bottom: 'var(--spacing-6)', right: 'var(--spacing-8)', textAlign: 'right' }}>
                <p style={{ color: 'var(--text-muted)', fontSize: '0.875rem', margin: '0 0 var(--spacing-1) 0' }}>Date Awarded</p>
                <p style={{ color: 'var(--text-light)', fontWeight: '600', margin: 0 }}>{new Date(selectedAchievement.date).toLocaleDateString()}</p>
              </div>
            </div>

            <div style={{ display: 'flex', justifyContent: 'center', gap: 'var(--spacing-4)', marginTop: 'var(--spacing-6)' }}>
              <Button variant="secondary" onClick={() => setSelectedAchievement(null)}>
                Close
              </Button>
              <Button
                variant="primary"
                onClick={handleDownloadPDF}
                style={{ display: 'flex', alignItems: 'center', gap: 'var(--spacing-2)' }}
              >
                <Download size={16} />
                Download PDF
              </Button>
            </div>
          </div>
        )}
      </Modal>
    </div>
  );
};

import React, { createContext, useState, useContext } from 'react';
import { generateStudentId, generateMentorId } from './idGenerator';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    const saved = localStorage.getItem('user');
    return saved ? JSON.parse(saved) : null;
  });

  const [usersDB, setUsersDB] = useState(() => {
    const saved = localStorage.getItem('saams_users_db');
    return saved ? JSON.parse(saved) : [];
  });

  const registerUser = (userData) => {
    let generatedId;
    if (userData.role === 'student') {
      generatedId = generateStudentId();
    } else if (userData.role === 'mentor') {
      generatedId = generateMentorId(userData.name);
    }
    const newUser = {
      ...userData,
      id: generatedId,
      photo: null,
      bio: 'Passionate learner and achiever',
      createdAt: new Date().toISOString(),
      adminId: userData.role === 'mentor' ? userData.adminId : null,
    };

    const newDB = [...usersDB, newUser];
    setUsersDB(newDB);
    localStorage.setItem('saams_users_db', JSON.stringify(newDB));

    const sessionUser = {
      id: generatedId,
      email: userData.email,
      role: userData.role,
      name: userData.name,
      photo: null,
      bio: 'Passionate learner and achiever',
      dob: userData.dob,
      mobileNo: userData.mobileNo,
      createdAt: newUser.createdAt,
      adminId: newUser.adminId,
      isAuthenticated: true
    };
    setUser(sessionUser);
    localStorage.setItem('user', JSON.stringify(sessionUser));
    return sessionUser;
  };

  const login = (identifier, password, role, name = '') => {
    if (role === 'superadmin') {
      const sessionUser = {
        id: identifier,
        email: `${identifier}@saams.com`,
        role: 'superadmin',
        name: name || 'System Creator',
        photo: null,
        bio: 'Master System Administrator',
        dob: '1990-01-01',
        mobileNo: '+1 (555) 000-0000',
        createdAt: new Date().toISOString(),
        isAuthenticated: true
      };
      setUser(sessionUser);
      localStorage.setItem('user', JSON.stringify(sessionUser));
      return sessionUser;
    }

    if (role === 'admin') {
      // In the future this will check the DB for the admin.
      // For now, allow dynamically logged in admin (created by superadmin) or fallback.
      const foundAdmin = usersDB.find(u => (u.email === identifier || u.id === identifier) && u.role === 'admin');

      let sessionUser;
      if (foundAdmin) {
        if (foundAdmin.password !== password) {
          throw new Error('Incorrect password');
        }
        sessionUser = { ...foundAdmin, isAuthenticated: true };
      } else {
        // Fallback or demo admin
        sessionUser = {
          id: identifier,
          email: `${identifier}@saams.com`,
          role: 'admin',
          name: name || 'Admin',
          photo: null,
          bio: 'University Administrator',
          dob: '1990-01-01',
          mobileNo: '+1 (555) 999-9999',
          createdAt: new Date().toISOString(),
          isAuthenticated: true
        };
      }
      setUser(sessionUser);
      localStorage.setItem('user', JSON.stringify(sessionUser));
      return sessionUser;
    }

    const foundUser = usersDB.find(u => u.email === identifier || u.id === identifier);
    if (!foundUser) {
      throw new Error('User not found. Please check your ID or Email.');
    }
    if (foundUser.password !== password) {
      throw new Error('Incorrect password');
    }

    const sessionUser = {
      id: foundUser.id,
      email: foundUser.email,
      role: foundUser.role,
      name: foundUser.name,
      photo: foundUser.photo,
      bio: foundUser.bio || 'Passionate learner and achiever',
      dob: foundUser.dob,
      mobileNo: foundUser.mobileNo,
      createdAt: foundUser.createdAt || new Date().toISOString(),
      isAuthenticated: true
    };
    setUser(sessionUser);
    localStorage.setItem('user', JSON.stringify(sessionUser));
    return sessionUser;
  };

  const updateUser = (updates) => {
    const updatedUser = { ...user, ...updates };
    setUser(updatedUser);
    localStorage.setItem('user', JSON.stringify(updatedUser));

    const newDB = usersDB.map(u => u.id === user.id ? { ...u, ...updates } : u);
    setUsersDB(newDB);
    localStorage.setItem('saams_users_db', JSON.stringify(newDB));
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  const createUniversityAdmin = (adminData) => {
    // Check if ID already exists
    if (usersDB.some(u => u.id === adminData.id)) {
      throw new Error('An Admin with this ID already exists');
    }

    const newAdmin = {
      ...adminData,
      role: 'admin',
      photo: null,
      bio: 'University Administrator',
      createdAt: new Date().toISOString(),
    };

    const newDB = [...usersDB, newAdmin];
    setUsersDB(newDB);
    localStorage.setItem('saams_users_db', JSON.stringify(newDB));
    return newAdmin;
  };

  return (
    <AuthContext.Provider value={{ user, usersDB, login, logout, registerUser, updateUser, createUniversityAdmin }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within AuthProvider');
  }
  return context;
};

import React, { createContext, useState, useContext, useEffect } from 'react';

const ThemeContext = createContext();

export const ThemeProvider = ({ children }) => {
    const [theme, setTheme] = useState(() => {
        // Check local storage or default to 'light'
        const savedTheme = localStorage.getItem('theme');
        if (savedTheme) {
            return savedTheme;
        }
        // Check system preference
        if (window.matchMedia && window.matchMedia('(prefers-color-scheme: dark)').matches) {
            return 'dark';
        }
        return 'light';
    });

    useEffect(() => {
        const root = document.documentElement;
        if (theme === 'dark') {
            root.classList.add('dark');
        } else {
            root.classList.remove('dark');
        }
        localStorage.setItem('theme', theme);
    }, [theme]);

    const toggleTheme = () => {
        setTheme(prevTheme => (prevTheme === 'light' ? 'dark' : 'light'));
    };

    return (
        <ThemeContext.Provider value={{ theme, setTheme, toggleTheme }}>
            {children}
        </ThemeContext.Provider>
    );
};

export const useTheme = () => {
    const context = useContext(ThemeContext);
    if (context === undefined) {
        throw new Error('useTheme must be used within a ThemeProvider');
    }
    return context;
};
